import * as style from "./list_of_studente.styles.ts";
import 'reactjs-popup/dist/index.css';
import StudentPopup from './StudentPopup';
import { useState, useEffect } from "react";
import StudentPopupadit from "./adit.student.js"
import {db} from '../firebase.js'
import { getDocs, collection} from "firebase/firestore";











function Students () {



  const [studentlist,setstudentlist] = useState([{
    first_name:'', last_name:'' ,phon_number:'', email:'',clas:''
    }])

    const fetchStudents = async () => {
      const querySnapshot = await getDocs(collection(db, "student"));
      const studentsData = [];
      querySnapshot.forEach((doc) => {
        studentsData.push(doc.data());
      });
      setstudentlist(studentsData);
    };

    useEffect(() => {
      fetchStudents();
    }, []);


  const deleteFunc = (index) => {
    const helpState = [...studentlist];
    helpState.splice(index, 1)
    setstudentlist(helpState)

    }


    return (
    <style.HomePage>
      <div > 
        <style.ButtonsContainer>
          <StudentPopup  setstudentlist = {setstudentlist} fech = {fetchStudents} />
        </style.ButtonsContainer>

        <style.StudenetTopic>    
          <div > First name     </div>
          <div > Lest name      </div>
          <div > Phone number   </div>
          <div > Email          </div>
          <div > Class          </div>
          <div >           </div>

        </style.StudenetTopic  >

        {studentlist.map((item, index) => (
          <style.StudenetLIst key = {index}>  

            <div> {item.first_name}</div> 
            <div> {item.last_name}</div> 
            <div> {item.phon_number}</div> 
            <div>{item.email}</div> 
            <div>{item.clas}</div>
            {item.first_name !== '' && (<div><button onClick={() => deleteFunc(index)}>delete</button>
            <StudentPopupadit  setstudentlist={setstudentlist} /></div>)}

          </style.StudenetLIst  > 
        ))}
      </div>
      </style.HomePage>
    );
  }

export default Students
