import * as style from "./list_of_studente.styles.ts";
import 'reactjs-popup/dist/index.css';
import StudentPopup from './StudentPopup';
import { useState, useEffect } from "react";
import StudentPopupadit from "./adit.student.js"
import {db} from '../firebase.js'
import {deleteDoc, getDocs, collection, doc} from "firebase/firestore";




function Students () {


  const [studentlist,setstudentlist] = useState([{}])

    const fetchStudents = async () => {
      const querySnapshot = await getDocs(collection(db, "student"));
      const studentsData = [];
      querySnapshot.forEach((doc) => {
        studentsData.push({id: doc.id,...doc.data()});
        console.log(doc.id)
      });
      setstudentlist(studentsData);
    };

    useEffect(() => {
      fetchStudents();
    }, []);


    const deleteStudent = async (studentId) => {

        await deleteDoc(doc(db, "student", studentId));
        console.log("Document successfully deleted!");
        fetchStudents();
    };


    return (
    <style.HomePage>
      <div > 
        <style.ButtonsContainer>
          <StudentPopup   fech = {fetchStudents} />
        </style.ButtonsContainer>

        <style.StudenetTopic>    
          <div > First name     </div>
          <div > Lest name      </div>
          <div > Phone number   </div>
          <div > Email          </div>
          <div > Class          </div>
          <div >           </div>

        </style.StudenetTopic  >

        {studentlist.map((item) => (
          <style.StudenetLIst >  
            <div>{item.id}</div>
            <div> {item.first_name}</div> 
            <div> {item.last_name}</div> 
            <div> {item.phon_number}</div> 
            <div>{item.email}</div> 
            <div>{item.clas}</div>
            {item.first_name !== '' && (<div><button onClick={() => deleteStudent(item.id)}>delete</button>
            <StudentPopupadit  setstudentlist={setstudentlist} /></div>)}

          </style.StudenetLIst  > 
        ))}
      </div>
      </style.HomePage>
    );
  }

export default Students
